import { createSlice } from '@reduxjs/toolkit'

const todosSlice = createSlice({
  name: 'todos',
  initialState:{value: ""},
  reducers: {
    onsearchChanging(state, action) {
      state.search =action.payload
    }
},
});

export const { onChanging } = todosSlice.actions
export default todosSlice.reducer
