import React, { useState } from "react";

function App(pros) {
  const [count, setCount] = useState(0);

  function spreadingSquares(value) {
    let tempArr = [];
    for (let i = 0; i < count; i++) {
      tempArr.push(i);
    }
    return tempArr;
  }
  return (
    <div className="App">
      <span className="add-cont">
        <span className="button" onClick={() => setCount(count - 1)}>
          -
        </span>
        <span className="button">{count}</span>
        <span className="button" onClick={() => setCount(count + 1)}>
          +
        </span>
      </span>
      <div className="display-grid">
        {spreadingSquares(count).map((item) => (
          <span className="sqaure"></span>
        ))}
      </div>
    </div>
  );
}

export default App;
